angular.module('managerApp').controller('CloudProjectComputeVolumeSnapshotAddCtrl', ["$scope", "$stateParams", "$uibModalInstance", "params", "CloudMessage", "$translate", "$filter", "$q", "OvhCloudPriceHelper", "CloudProjectComputeVolumesOrchestrator", function CloudProjectComputeVolumeSnapshotAddCtrl($scope, $stateParams, $uibModalInstance, params, CloudMessage, $translate, $filter, $q, OvhCloudPriceHelper, CloudProjectComputeVolumesOrchestrator) {
  var self = this;
  var serviceName = $stateParams.projectId;
  self.snapshot = {
    volume: params,
    name: null,
    price: null,
    priceText: null
  };
  self.loaders = {
    init: false,
    snapshot: false
  };

  function init() {
    self.loaders.init = true;
    var volumeSnapshotConsumption = 'volume.snapshot.consumption';
    OvhCloudPriceHelper.getPrices(serviceName).then(function (prices) {
      var price = prices["".concat(volumeSnapshotConsumption, ".").concat(self.snapshot.volume.region)] || prices[volumeSnapshotConsumption];

      if (price) {
        self.snapshot.price = price.priceInUcents * self.snapshot.volume.size * moment.duration(1, 'months').asHours() / 100000000;
        self.snapshot.priceText = price.price.text.replace(/\d+(?:[.,]\d+)?/, _.round(self.snapshot.price.toString(), 2));
      }

      self.snapshot.name = "".concat(self.snapshot.volume.name, " ").concat($filter('date')(new Date(), 'short'));
    }).finally(function () {
      self.loaders.init = false;
    });
  }

  self.confirmSnapshot = function confirmSnapshot() {
    self.loaders.snapshot = true;
    CloudProjectComputeVolumesOrchestrator.snapshotVolume(self.snapshot.volume, self.snapshot.name).then(function () {
      $uibModalInstance.close(self.snapshot);
      CloudMessage.info($translate.instant('cpcv_snapshot_creation_info'), {
        hideAfter: 3
      });
    }, function (err) {
      CloudMessage.error([$translate.instant('cpcv_snapshot_creation_error'), err.data && err.data.message || ''].join(' '));
    }).finally(function () {
      self.loaders.snapshot = false;
    });
  };

  self.cancel = function cancel() {
    $uibModalInstance.dismiss(self.snapshot);
  };

  init();
}]);
