angular.module('managerApp').directive('hterm', ["$interval", function ($interval) {
  return {
    restrict: 'EA',
    scope: {
      sendData: '&',
      sendConfig: '&',
      term: '='
    },
    link: function link(scope, element) {
      Terminal.applyAddon(fit);
      var term = scope.term || new Terminal();
      term.on('data', function (data) {
        scope.sendData({
          data: data
        });
      });
      term.on('resize', function (size) {
        return scope.sendConfig({
          config: {
            columns: size.cols,
            rows: size.rows
          }
        });
      });
      term.open(element.context);
      var interval = $interval(function () {
        term.fit();
      }, 600);
      scope.$on('$destroy', function () {
        if (interval) {
          interval.cancel();
        }
      });
    }
  };
}]);
