angular.module('managerApp').controller('DeskaasChangePasswordCtrl', ["$uibModalInstance", "OvhApiDeskaasService", "service", function ($uibModalInstance, OvhApiDeskaasService, service) {
  var self = this;
  self.policies = {};
  self.values = {
    password: '',
    generatePwd: false
  };
  self.flags = {
    init: false
  };

  self.cancel = function () {
    $uibModalInstance.dismiss('cancel');
  };

  self.ok = function () {
    if (!self.values.generatePwd && !self.values.password) {
      $uibModalInstance.dismiss('cancel');
      return;
    }

    $uibModalInstance.close(self.values);
  };

  function createExpression(policies) {
    var exp = ''; // lookaheads to enforce mandatory patterns

    if (policies.letterMandatory) {
      exp += '(?=.*[a-zA-Z])';
    }

    if (policies.lowercaseLetterMandatory) {
      exp += '(?=.*[a-z])';
    }

    if (policies.uppercaseLetterMandatory) {
      exp += '(?=.*[A-Z])';
    }

    if (policies.digitMandatory) {
      exp += '(?=.*\\d)';
    }

    exp += '[^'; // match to filter on invalid chars and length

    if (policies.deniedChars && policies.deniedChars.length > 0) {
      _.forEach(policies.deniedChars, function (char) {
        exp += "\\x".concat(char.charCodeAt(0).toString(16));
      });
    }

    exp += "\\s]{".concat(policies.minLength, ",").concat(policies.maxLength, "}");
    return exp;
  }

  function init() {
    self.flags.init = true;
    OvhApiDeskaasService.v6().getPwdPolicy({
      serviceName: service
    }, null).$promise.then(function (policies) {
      self.policies = policies;
      self.policies.deniedCharsString = policies.deniedChars.join(', ');
      self.policies.pattern = createExpression(policies);
    }).finally(function () {
      self.flags.init = false;
    });
  }

  init();
}]);
