var frenchTouch = {
  TTCOnly: false,
  HTOnly: false,
  withTTC: true
};
var usTouch = {
  TTCOnly: false,
  HTOnly: true,
  withTTC: false
};
var deutchTouch = {
  TTCOnly: true,
  HTOnly: false,
  withTTC: true
};
var showTaxes = {
  CA: usTouch,
  WE: usTouch,
  WS: usTouch,
  QC: usTouch,
  DE: deutchTouch,
  FI: deutchTouch,
  SN: deutchTouch,
  CZ: frenchTouch,
  ES: frenchTouch,
  FR: frenchTouch,
  GB: frenchTouch,
  IE: frenchTouch,
  IT: frenchTouch,
  LT: frenchTouch,
  MA: frenchTouch,
  NL: frenchTouch,
  PL: frenchTouch,
  PT: frenchTouch,
  TN: frenchTouch
};

function priceFilter($translate) {
  function format(price, country, frequency) {
    var theCountry = country || 'FR';
    var taxes = showTaxes[theCountry];

    if (price.withTax.value !== 0) {
      if (taxes.TTCOnly) {
        return "<span class=\"bold\">".concat(price.withTax.text, "</span>");
      }

      if (taxes.HTOnly) {
        return "<span class=\"bold\">".concat(price.withoutTax.text, "</span>");
      }

      if (frequency === 'yearly') {
        return "<span class=\"bold\">".concat($translate.instant('common_price_ht_label', {
          price: price.withoutTax.text
        })) + "<small>".concat($translate.instant('common_price_label_yearly'), "</small></span> ") + "<span class=\"italic small\"> (".concat($translate.instant('common_price_ttc_label', {
          price: price.withTax.text
        })) + "<small>".concat($translate.instant('common_price_label_yearly'), "</small>)</span>");
      }

      return "<span class=\"bold\">".concat($translate.instant('common_price_ht_label', {
        price: price.withoutTax.text
      })) + "</span> <span class=\"italic small\"> (".concat($translate.instant('common_price_ttc_label', {
        price: price.withTax.text
      }), ")</span>");
    }

    return "<span class=\"bold\">".concat($translate.instant('common_price_free'), "</span>");
  }

  return function (price, ovhSubsidiary, frequency) {
    if (price) {
      return format(price, ovhSubsidiary, frequency);
    }

    return '<span/>';
  };
}

angular.module('managerApp').filter('price', ['$translate', priceFilter]);
