/*----------- opacity 1 to n and n to 1 -----------*/
@keyframes animate-opacity9-enter {
  0% {
    opacity: 0.9;
  }
  100% {
    opacity: 1;
  }
}
.opacity9-enter {
  animation: animate-opacity9-enter 500ms;
  opacity: 1;
}
.animate-opacity9-enter.ng-enter,
.animate-opacity9-enter.ng-hide-remove {
  animation: animate-opacity9-enter 500ms;
  opacity: 1;
}
@keyframes animate-opacity9-leave {
  0% {
    opacity: 1;
  }
  100% {
    opacity: 0.9;
  }
}
.opacity9-leave {
  animation: animate-opacity9-leave 500ms;
  opacity: 0.9;
}
