/*----------- shake-light -----------*/
@keyframes shake-light {
  0%,
  100% {
    transform: translate3d(0, 0, 0);
  }
  10%,
  70% {
    transform: translate3d(-3px, 0, 0);
  }
  40%,
  100% {
    transform: translate3d(3px, 0, 0);
  }
}
.shake-light {
  animation-name: shake-light;
}
.animate-shake-light-enter.ng-enter,
.animate-shake-light-enter.ng-hide-remove {
  animation: shake-light 200ms;
}
.animate-shake-light-leave.ng-leave,
.animate-shake-light-leave.ng-hide-add {
  animation: shake-light 200ms;
}
/*----------- inputs -----------*/
input.ng-invalid.ng-dirty.error-animate-shake-light,
textarea.ng-invalid.ng-dirty.error-animate-shake-light {
  animation: shake-light 200ms;
}
input.ng-invalid.ng-dirty.error-animate-shake,
textarea.ng-invalid.ng-dirty.error-animate-shake {
  animation: shake 200ms;
}
