ovh-slider,
[ovh-slider] {
  display: inline-block;
  position: relative;
  height: 7px;
  width: 100%;
  margin: 10px 0 15px 0px;
  vertical-align: middle;
}
ovh-slider div,
[ovh-slider] div {
  white-space: nowrap;
  position: absolute;
}
ovh-slider div.bar,
[ovh-slider] div.bar {
  width: 100%;
  height: 100%;
  border-radius: 7px;
  background: #F1F1F1;
  overflow: hidden;
}
ovh-slider div.bar .selection,
[ovh-slider] div.bar .selection {
  width: 0%;
  height: 100%;
  background: #13b6ff;
}
ovh-slider div.bar.disabled,
[ovh-slider] div.bar.disabled {
  cursor: not-allowed;
  opacity: .3;
}
ovh-slider div.handle,
[ovh-slider] div.handle {
  cursor: pointer;
  width: 15px;
  height: 15px;
  top: -4px;
  background-color: #8d919c;
  border: 1px solid #7f8490;
  z-index: 2;
  border-radius: 3px;
}
ovh-slider div.handle:hover,
[ovh-slider] div.handle:hover {
  background-color: #009fe3;
  border: 1px solid #7f8490;
}
ovh-slider div.handle.disabled,
[ovh-slider] div.handle.disabled {
  background: #b6b9c0;
  border: 1px solid #a8abb4;
  cursor: not-allowed;
  opacity: .3;
}
ovh-slider div.bubble,
[ovh-slider] div.bubble {
  display: none;
  cursor: default;
  top: -22px;
  padding: 1px 3px 1px 3px;
  font-size: 0.8em;
}
ovh-slider div.bubble.active,
[ovh-slider] div.bubble.active {
  display: inline-block;
}
ovh-slider div.bubble.limit,
[ovh-slider] div.bubble.limit {
  color: #777;
}
ovh-slider div.bubble.value.disabled,
[ovh-slider] div.bubble.value.disabled {
  display: none;
}
ovh-slider div.markers,
[ovh-slider] div.markers {
  height: 10px;
  margin: auto;
  margin-top: 15px;
}
ovh-slider div.marker,
[ovh-slider] div.marker {
  width: 2px;
  height: 5px;
  margin-left: -1px;
  position: absolute;
  background-color: #B9B9B9;
}
ovh-slider div.markerLabel,
[ovh-slider] div.markerLabel {
  width: 40px;
  margin-left: -20px;
  text-align: center;
  left: 0%;
  padding-top: 10px;
  color: #797979;
  font-size: x-small;
}
.ovh-angular-tail-logs {
  background-color: #122844;
  color: white;
  font-family: monospace;
  padding: 10px;
  height: 500px;
  overflow-y: auto;
  overflow-x: hidden;
}
.ovh-angular-tail-logs__cursor {
  content: "";
  display: inline-block;
  background-color: white;
  vertical-align: top;
  width: 8px;
  height: 17px;
  animation: ovh-tail-logs-blink 1s step-end infinite;
}
@keyframes ovh-tail-logs-blink {
  0% {
    opacity: 1.0;
  }
  50% {
    opacity: 0.0;
  }
  100% {
    opacity: 1.0;
  }
}
