body.responsive-popover-fixed-body {
  position: fixed;
}
.responsive-popover {
  max-width: inherit !important;
}
.responsive-popover.popover-full-screen {
  position: fixed;
  top: 10px !important;
  bottom: 10px !important;
  left: 10px !important;
  right: 10px !important;
  margin: 0;
  overflow-y: auto;
}
.responsive-popover.popover-full-screen .arrow {
  display: none;
}
[responsive-popover-popup].popover.top-left > .arrow,
[responsive-popover-popup].popover.top-right > .arrow,
[responsive-popover-popup].popover.bottom-left > .arrow,
[responsive-popover-popup].popover.bottom-right > .arrow,
[responsive-popover-popup].popover.left-top > .arrow,
[responsive-popover-popup].popover.left-bottom > .arrow,
[responsive-popover-popup].popover.right-top > .arrow,
[responsive-popover-popup].popover.right-bottom > .arrow {
  top: auto;
  bottom: auto;
  left: auto;
  right: auto;
  margin: 0;
}
[responsive-popover-popup].popover {
  display: block !important;
}
.responsive-switch {
  overflow: hidden;
}
.responsive-switch_mobile .responsive-switch-page-right,
.responsive-switch_mobile .responsive-switch-page-left {
  width: 100%;
}
.responsive-switch.responsive-switch-switch {
  position: relative;
  width: 100%;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-left .responsive-switch-page-wrapper.ng-enter {
  left: -100%;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-left .responsive-switch-page-wrapper.ng-enter.ng-enter-active {
  transform: translateX(100%);
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-left .responsive-switch-page-wrapper.ng-leave.ng-leave-active {
  transform: translateX(-100%);
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-enter {
  left: 100%;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-enter.ng-enter-active {
  transform: translateX(-100%);
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-leave {
  left: 0;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-leave.ng-leave-active {
  transform: translateX(100%);
}
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper {
  position: relative;
  overflow-x: hidden;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper.ng-enter,
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper.ng-leave {
  transition: transform 150ms cubic-bezier(0.25, 0.25, 0.75, 0.75);
  transform: translateX(0px);
  top: 0;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper.ng-enter {
  position: relative;
  opacity: 0;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper.ng-enter.ng-enter-active {
  opacity: 1;
}
.responsive-switch.responsive-switch-switch .responsive-switch-page .responsive-switch-page-wrapper.ng-leave {
  position: absolute;
}
.responsive-switch.responsive-switch-sidebyside {
  display: flex;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page {
  display: flex;
  align-items: stretch;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper {
  overflow-x: hidden;
  animation-duration: 0.5s;
  animation-timing-function: ease;
  animation-fill-mode: backwards;
  display: block;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-enter {
  animation-name: horizontalexpand;
  animation-play-state: paused;
  visibility: hidden;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-enter.ng-enter-active {
  visibility: visible;
  animation-play-state: running;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-leave {
  animation-name: horizontalcollapse;
  animation-play-state: paused;
}
.responsive-switch.responsive-switch-sidebyside .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper.ng-leave.ng-leave-active {
  animation-play-state: running;
}
.responsive-switch .responsive-switch-page {
  float: left;
}
.responsive-switch .responsive-switch-page.responsive-switch-page-right {
  overflow-y: auto;
  overflow-x: hidden;
}
.responsive-switch .responsive-switch-page.responsive-switch-page-right .responsive-switch-page-wrapper .responsive-switch-page-content {
  padding: 10px;
}
.responsive-switch .responsive-switch-page .responsive-switch-page-wrapper {
  min-height: 100px;
}
.responsive-switch .responsive-switch-page .responsive-switch-page-wrapper .responsive-switch-page-content {
  overflow: auto;
  min-height: 100px;
}
@keyframes horizontalexpand {
  from {
    max-width: 0px;
  }
  to {
    max-width: 500px;
  }
}
@keyframes horizontalcollapse {
  from {
    max-width: 500px;
  }
  to {
    max-width: 0px;
  }
}
