  bottom: 0px !important;
  right: 0px !important;
  left: auto !important;
  top: auto !important;
}
.otrs-popup.maximize {
  height: 100% !important;
  width: 100% !important;
  top: 0 !important;
  left: 0 !important;
  overflow: auto;
}
.otrs-popup .draggable-body {
