angular.module('managerApp').controller('CdaPoolListCtrl', ["$q", "$stateParams", "$uibModal", "$translate", "OvhApiDedicatedCeph", "CloudMessage", function ($q, $stateParams, $uibModal, $translate, OvhApiDedicatedCeph, CloudMessage) {
  var self = this;
  self.loading = false;
  self.datas = {
    pools: undefined
  };
  self.modals = {
    add: {
      templateUrl: 'app/cda/pool/add/cda-pool-add.html',
      controller: 'CdaPoolAddCtrl'
    },
    remove: {
      templateUrl: 'app/cda/pool/delete/cda-pool-delete.html',
      controller: 'CdaPoolDeleteCtrl'
    }
  };

  function initPools() {
    OvhApiDedicatedCeph.Pool().v6().resetAllCache();
    return OvhApiDedicatedCeph.Pool().v6().query({
      serviceName: $stateParams.serviceName
    }).$promise.then(function (pools) {
      self.datas.pools = pools;
      return pools;
    });
  }

  function displayError(error) {
    CloudMessage.error([$translate.instant('ceph_common_error'), error.data && error.data.message || ''].join(' '));
  }

  function init() {
    self.loading = true;
    initPools().catch(function (error) {
      displayError(error);
    }).finally(function () {
      self.loading = false;
    });
  }

  self.openAddModal = function () {
    self.openModal(self.modals.add.templateUrl, self.modals.add.controller);
  };

  self.openDeleteModal = function (pool) {
    self.openModal(self.modals.remove.templateUrl, self.modals.remove.controller, {
      pool: pool
    });
  };

  self.openModal = function (template, controller, params) {
    var modal = $uibModal.open({
      windowTopClass: 'cui-modal',
      templateUrl: template,
      controller: controller,
      controllerAs: controller,
      resolve: {
        items: function items() {
          return params;
        }
      }
    });
    modal.result.then(function () {
      initPools();
    });
  };

  init();
}]);
