angular.module('managerApp').controller('CdaIpListCtrl', ["$q", "$stateParams", "$uibModal", "$translate", "OvhApiDedicatedCeph", "CloudMessage", function ($q, $stateParams, $uibModal, $translate, OvhApiDedicatedCeph, CloudMessage) {
  var self = this;
  self.loading = false;
  self.datas = {
    ips: undefined
  };
  self.modals = {
    add: {
      templateUrl: 'app/cda/ip/add/cda-ip-add.html',
      controller: 'CdaIpAddCtrl'
    },
    remove: {
      templateUrl: 'app/cda/ip/delete/cda-ip-delete.html',
      controller: 'CdaIpDeleteCtrl'
    }
  };

  function initIps() {
    OvhApiDedicatedCeph.Acl().v6().resetAllCache();
    return OvhApiDedicatedCeph.Acl().v6().query({
      serviceName: $stateParams.serviceName
    }).$promise.then(function (ips) {
      self.datas.ips = ips;
      return ips;
    });
  }

  function displayError(error) {
    CloudMessage.error([$translate.instant('ceph_common_error'), error.data && error.data.message || ''].join(' '));
  }

  function init() {
    self.loading = true;
    initIps().catch(function (error) {
      displayError(error);
    }).finally(function () {
      self.loading = false;
    });
  }

  self.openAddModal = function () {
    self.openModal(self.modals.add.templateUrl, self.modals.add.controller);
  };

  self.openDeleteModal = function (ip) {
    self.openModal(self.modals.remove.templateUrl, self.modals.remove.controller, {
      ip: ip
    });
  };

  self.openModal = function (template, controller, params) {
    var modal = $uibModal.open({
      windowTopClass: 'cui-modal',
      templateUrl: template,
      controller: controller,
      controllerAs: controller,
      resolve: {
        items: function items() {
          return params;
        }
      }
    });
    modal.result.then(function () {
      initIps();
    });
  };

  init();
}]);
