angular.module('managerApp').controller('NashaPartitionCustomSnapshotAddCtrl', ["$scope", "$stateParams", "$translate", "$uibModalInstance", "OvhApiDedicatedNashaPartition", "CloudMessage", function ($scope, $stateParams, $translate, $uibModalInstance, OvhApiDedicatedNashaPartition, CloudMessage) {
  var self = this;
  self.saving = false;
  self.data = {
    partition: $scope.$resolve.items,
    customSnapshot: {
      prefix: 'snap-'
    }
  };
  self.options = {
    snapshotName: {
      maxLength: 70
    }
  };
  self.snapshotName = '';

  function computeDefaultSnapshotName() {
    var currentDate = new Date();
    var timeZonedDate = new Date(currentDate.getTime() - currentDate.getTimezoneOffset() * 60000);
    var dateString = timeZonedDate.toISOString();
    self.snapshotName = "".concat(self.data.partition.partitionName, "-").concat(dateString);
  }

  function init() {
    computeDefaultSnapshotName();
  }

  self.addCustomSnapshot = function () {
    self.saving = true;
    OvhApiDedicatedNashaPartition.CustomSnapshot().v6().add({
      serviceName: $stateParams.nashaId,
      partitionName: self.data.partition.partitionName
    }, {
      name: self.snapshotName
    }).$promise.then(function (result) {
      $uibModalInstance.close({
        partition: self.data.partition,
        tasks: [result.data.taskId]
      });
      CloudMessage.success($translate.instant('nasha_custom_snapshot_modal_success'));
    }).catch(function () {
      $uibModalInstance.dismiss();
      CloudMessage.error($translate.instant('nasha_custom_snapshot_modal_fail'));
    }).finally(function () {
      self.saving = false;
    });
  };

  self.dismiss = function () {
    $uibModalInstance.dismiss();
  };

  init();
}]);
