angular.module('managerApp').filter('bytes', ["$translate", function ($translate) {
  // TODO: Add this filter in UX components
  var units = ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
  var unitsKibi = ['B', 'KiB', 'MiB', 'GiB', 'TiB', 'PiB', 'EiB', 'ZiB', 'YiB'];

  function translateUnit(unit) {
    var key = "unit_size_".concat(unit);
    var translatedUnit = $translate.instant(key); // if translation is not found use the default unit

    return key === translatedUnit ? unit : translatedUnit;
  }

  var translatedUnits = _.map(units, translateUnit);

  var translatedUnitsKibi = _.map(unitsKibi, translateUnit);

  return function (bytesParams, precisionParam, toKibi, fromUnit, toRawBytes) {
    var bytes = bytesParams;
    var precision = precisionParam;

    if (fromUnit) {
      var fromKibiUnitIndex = _.indexOf(unitsKibi, fromUnit);

      var fromUnitIndex = _.indexOf(units, fromUnit);

      if (fromKibiUnitIndex !== -1) {
        if (fromKibiUnitIndex > 0) {
          bytes *= Math.pow(1024, fromKibiUnitIndex); // eslint-disable-line
        }
      } else if (fromUnitIndex !== -1) {
        if (fromUnitIndex > 0) {
          bytes *= Math.pow(1000, fromUnitIndex); // eslint-disable-line
        }
      } else {
        return '?';
      }
    }

    if (toRawBytes === true) {
      return bytes;
    }

    if (bytes === 0) {
      return '0';
    }

    if (Number.isNaN(parseFloat(bytes)) || !Number.isFinite(bytes)) {
      return '?';
    }

    if (typeof precision === 'undefined') {
      precision = 0;
    }

    var divider = toKibi ? 1024 : 1000;
    var number = Math.floor(Math.log(bytes) / Math.log(divider));
    var value = (bytes / Math.pow(divider, Math.floor(number))).toFixed(precision); // eslint-disable-line

    if (/\.0+$/.test(value)) {
      value = value.replace(/\.0+$/, '');
    }

    return "".concat(value, " ").concat(toKibi ? translatedUnitsKibi[number] : translatedUnits[number]);
  };
}]);
