angular.module('managerApp').directive('cuiPassword', ["$compile", function ($compile) {
  return {
    restrict: 'A',
    // the input will not work if there are more than 1 cui-password in a given scope / controller.
    // Will fix later.
    scope: false,
    replace: true,
    link: function link($scope, $element) {
      $scope.showPassword = false;
      $scope.$watch(function () {
        return $scope.showPassword;
      }, function () {
        $element.attr('type', $scope.showPassword ? 'text' : 'password');
      });
      var buttons = angular.element("\n        <button class=\"oui-button\" type=\"button\" aria-label=\"Show password\" data-ng-click=\"showPassword = !showPassword\" data-ng-show=\"!showPassword\">\n          <i class=\"oui-icon oui-icon-eye\"></i>\n        </button>\n        <button class=\"oui-button\" type=\"button\" aria-label=\"Hide password\" data-ng-click=\"showPassword = !showPassword\" data-ng-show=\"showPassword\">\n          <i class=\"oui-icon oui-icon-eye-blocked\"></i>\n        </button>\n      ");
      $compile(buttons)($scope);
      var parent = $element[0].parentElement;

      if (_.includes(parent.classList, 'oui-input-group')) {
        buttons.appendTo(parent);
      } else if (_.includes(parent.classList, 'oui-field-control')) {
        var container = angular.element('<div class="oui-input-group oui-input-group_button"></div>');
        container.appendTo(parent);
        $element.appendTo(container);
        buttons.appendTo(container);
      }
    }
  };
}]);
