angular.module('managerApp').controller('CdaDetailsCtrl', ["$stateParams", "$translate", "CdaService", "CloudMessage", "URLS", "OvhApiMe", function ($stateParams, $translate, CdaService, CloudMessage, URLS, OvhApiMe) {
  var self = this;
  self.CdaService = CdaService;
  self.serviceName = '';
  self.messages = [];
  self.loading = true;
  self.guides = {
    title: $translate.instant('cda_guide_title'),
    list: [{
      name: $translate.instant('cda_guide_name')
    }],
    footer: $translate.instant('cda_guide_footer')
  };

  self.refreshMessage = function () {
    self.messages = self.messageHandler.getMessages();
  };

  self.loadMessage = function () {
    CloudMessage.unSubscribe('paas.cda');
    self.messageHandler = CloudMessage.subscribe('paas.cda', {
      onMessage: function onMessage() {
        return self.refreshMessage();
      }
    });
  };

  self.loadGuides = function () {
    return OvhApiMe.v6().get().$promise.then(function (me) {
      self.guides.list[0].url = "".concat(URLS.guides.home[me.ovhSubsidiary]).concat(URLS.guides.cda);
    }).catch(function (error) {
      CloudMessage.error("".concat($translate.instant('cda_guide_retrieval_error'), " ").concat(_(error).get('data.message', '')));
    });
  };

  function init() {
    self.serviceName = $stateParams.serviceName;
    self.loadMessage();
    self.loadGuides();
  }

  init();
}]);
